mapper.select:
SELECT id,memberId, purchaseStatus, invoiceStatus,supplyerName, purchaseNo, contractNo,customerName,purchaseWeight, purchaseAmount, purchaseDate, payment, createBy, verifyBy, remark, crt,upt FROM purchase_contract WHERE 1=1
AND supplyerName LIKE CONCAT('%',#{dto.keyword},'%')
AND purchaseStatus =#{statusTab}
AND invoiceStatus =#{invoicestatus}
AND DATE_FORMAT(purchaseDate,'%Y-%m-%d') >= DATE_FORMAT(#{startTime},'%Y-%m-%d')
AND DATE_FORMAT(purchaseDate,'%Y-%m-%d') <= DATE_FORMAT(#{endTime},'%Y-%m-%d')
AND createBy =#{createby}
order by purchaseDate desc
