mapper.select:
SELECT id,memberId, customerId, customerName, contractNo, contractAddress,contractDate,deliveryDate, contractWeight, contractAmount, actualWeight, actualAmount, payment, settlement, percent,depositDate, transportType, contractType, contractStatus, crt, upt,remark FROM sale_contract WHERE 1=1
AND customerName LIKE CONCAT('%',#{dto.keyword},'%')
AND customerId =#{dto.id}
AND TO_DAYS(NOW()) - TO_DAYS(contractDate) < #{searchDate}
order by crt desc

SELECT id,memberId, customerId, customerName, contractNo, contractAddress,contractDate,deliveryDate, contractWeight, contractAmount, actualWeight, actualAmount, payment, settlement, percent,depositDate, transportType, contractType, contractStatus, invoiceStatus, createBy,verifyBy,crt, upt,remark FROM sale_contract WHERE 1=1
AND customerName LIKE CONCAT('%',#{dto.keyword},'%')
AND contractStatus =#{statusTab}
AND createBy =#{createby}
AND contractType =#{contracttype}
AND invoiceStatus =#{invoicestatus}
AND DATE_FORMAT(contractDate,'%Y-%m-%d') >= DATE_FORMAT(#{startTime},'%Y-%m-%d')
AND DATE_FORMAT(contractDate,'%Y-%m-%d') <= DATE_FORMAT(#{endTime},'%Y-%m-%d ')
AND verifyBy is not NULL
order by crt desc

mapper.update:
update sale_contract
set 
  actualWeight = #{actualweight,jdbcType=DECIMAL},
  actualAmount = #{actualamount,jdbcType=DECIMAL},
  contractStatus = #{contractstatus,jdbcType=VARCHAR}
where contractNo = #{contractno,jdbcType=VARCHAR}

update sale_contract
set 
  actualWeight = actualWeight + #{actualweight,jdbcType=DECIMAL},
  actualAmount = actualAmount + #{actualamount,jdbcType=DECIMAL}
where contractNo = #{contractno,jdbcType=VARCHAR}

update.foreach:
UPDATE sale_contract

    actualWeight = actualWeight - #{bean.actualweight},
    actualAmount = actualAmount - #{bean.actualamount}
    


     contractNo = #{bean.contractno,jdbcType=VARCHAR}
